CREATE TABLE "COMPARED_SUITES" ("O_SUITE_DATE" TEXT NOT NULL , "O_SUITE" TEXT NOT NULL , "N_SUITE_DATE" TEXT NOT NULL , "N_SUITE" TEXT NOT NULL , "TEST" TEXT NOT NULL , "METHOD" TEXT NOT NULL , "STEP_NUMBER" INTEGER NOT NULL , "EXISTS_STEP_OLD" BOOL, "EXISTS_STEP_NEW" BOOL, "HTML_COMPARED" BOOL, "DIFFERENCES" BOOL, "INDEX_DIFF" FLOAT, "EXCEPTION" BOOL, "NUM_ADDS" INTEGER, "NUM_DELETEDS" INTEGER, "NUM_CHANGES" INTEGER, "O_INICIO" DATETIME, "O_FIN" DATETIME, "N_INICIO" DATETIME, "N_FIN" DATETIME, PRIMARY KEY ("O_SUITE_DATE", "O_SUITE", "N_SUITE_DATE", "N_SUITE", "TEST", "METHOD", "STEP_NUMBER"));
CREATE TABLE "CORREOS_ENVIADOS" ("SUITE_DATE" TEXT NOT NULL ,"SUITE" TEXT NOT NULL ,"TEST" TEXT NOT NULL ,"METHOD" TEXT NOT NULL ,"FECHA_ENVIO" TEXT, "DATE_ENVIO" DATE, PRIMARY KEY ("SUITE_DATE","SUITE","TEST","METHOD", "DATE_ENVIO") );
CREATE TABLE "METHODS" ("SUITE_DATE" TEXT NOT NULL ,"SUITE" TEXT NOT NULL ,"TEST" TEXT NOT NULL ,"METHOD" TEXT NOT NULL ,"RESULT_SCRIPT" INTEGER,"RESULT_TNG" INTEGER,"INICIO" DATETIME,"FIN" DATETIME,"TIME_MS" FLOAT,"NUMBER_STEPS" INTEGER,"CLASS_SIGNATURE" TEXT, "INSTANCIA" TEXT, PRIMARY KEY ("SUITE_DATE","SUITE","TEST","METHOD") );
CREATE TABLE "STATDATA_FOR_STEP" ("SUITE_DATE" STRING NOT NULL ,"SUITE" TEXT NOT NULL ,"TEST" TEXT NOT NULL ,"METHOD" TEXT NOT NULL ,"STEP_NUMBER" INTEGER NOT NULL , "COMPONENT" TEXT NOT NULL, "TYPE" TEXT NOT NULL, "MEDIA" TEXT, PRIMARY KEY ("SUITE_DATE","SUITE","TEST","METHOD","STEP_NUMBER","COMPONENT") );
CREATE TABLE "STATIC_DATA" ("SUITE_DATE" TEXT NOT NULL , "SUITE" TEXT NOT NULL , "COMPONENT" TEXT NOT NULL , "TYPE" TEXT, "TEST" TEXT, "METHOD" TEXT, "EXCEPCION" BOOL, "INICIO" DATETIME, "FIN" DATETIME, "TIME_MS" FLOAT, "SIZE" INTEGER check(typeof("SIZE") = 'integer') , "MEDIA" TEXT, PRIMARY KEY ("SUITE_DATE", "SUITE", "COMPONENT"));
CREATE TABLE "STEPS" ("SUITE_DATE" STRING NOT NULL ,"SUITE" TEXT NOT NULL ,"TEST" TEXT NOT NULL ,"METHOD" TEXT NOT NULL ,"STEP_NUMBER" INTEGER NOT NULL ,"DESCRIPTION" TEXT,"RES_EXPECTED" TEXT,"RESULTADO" INTEGER,"EXCEPCION" BOOL DEFAULT 0 ,"INICIO" DATETIME,"FIN" DATETIME,"TIME_MS" FLOAT,"NUM_VALIDATIONS" INTEGER DEFAULT 0 ,"IMAGE" BOOL,"HTML" , "TYPE_PAGE" INTEGER, PRIMARY KEY ("SUITE_DATE","SUITE","TEST","METHOD","STEP_NUMBER") );
