CREATE TABLE "TEST_USUARIO" ("CLASS" ,"TEST" TEXT,"GROUP" TEXT,"IDENTIFICADOR" STRING NOT NULL ,"ACTIVO" INTEGER NOT NULL ,"USUARIO_FIJO" TEXT,"ENTIDAD" TEXT);
CREATE TABLE "USUARIOS" ("COD_USER" INTEGER PRIMARY KEY  NOT NULL ,"IDENTIFICADOR" TEXT NOT NULL ,"ACTIVO" BOOL NOT NULL ,"PASSWORD" TEXT NOT NULL ,"FIRMA" TEXT,"COORDENADA" TEXT,"DESCRIPCION" TEXT,"CUENTA_PRINC" TEXT,"CONTRATO_NUEVO" ,"PRINCIPAL" ,"MOVIL" TEXT,"EMAIL" TEXT,"TARJETA_PRINC" TEXT, "CUENTA_P_CEP" CHAR, "ENTIDAD" CHAR);
CREATE TABLE "VALIDATIONS" ("SUITE_DATE" TEXT NOT NULL ,"SUITE" TEXT NOT NULL ,"TEST" TEXT NOT NULL ,"METHOD" TEXT NOT NULL ,"STEP_NUMBER" INTEGER NOT NULL ,"VALIDATION_NUMBER" INTEGER NOT NULL ,"DESCRIPTION" TEXT,"RESULTADO" INTEGER, PRIMARY KEY ("SUITE_DATE","SUITE","TEST","METHOD","STEP_NUMBER","VALIDATION_NUMBER") );
CREATE INDEX TESTNG ON SUITES ( TESTNG_INDEX );
